#!/usr/bin/env python3
from PIL import Image

img = Image.open('unimportant.png')
bits = []
for x in range(img.width):
    for y in range(img.height):
        pixel = img.getpixel((x, y))
        bits.append((pixel[1] >> 1) & 1)
        if len(bits) == 791:
            break
    if len(bits) == 791:
        break
x = ''.join(map(str, bits))
print(x)
print(hex(int(x, 2)))
